import { SIGN_IN, SIGN_OUT } from '../actions/types'

const INITIAL_STATE = {
    inSignedIn: null,
    userId: null
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case SIGN_IN:
          return { ...state, isSignedIn: true, userId: action.payload }
        case SIGN_OUT:
          return { ...state, isSignedIn: false }
        default:
          return state;
    }
};
