import React from 'react';
import { connect } from 'react-redux';
import { fetchStreams } from '../../actions';
import { Link } from 'react-router-dom';
import './StreamList.css'
import faker from 'faker';

class StreamList extends React.Component {

    state = { streams: {} }

    componentDidMount() {
        this.props.fetchStreams()
    }
    
    createButton() {
        if(this.props.isSignedIn){
            return (
                <div style={{ textAlign: 'right' }}>
                    <Link to="/streams/new" className="ui positive button ">CREATE STREAM</Link>
                </div>
            )   
        }
    }

    renderAdmin(stream) {
        if(stream.userId === this.props.currentUserId) {
            return (
                <div className="ui two buttons">
                    <Link to={`/streams/edit/${stream.id}`} className="ui button primary">
                        EDIT
                    </Link>
                    <Link to={`/streams/delete/${stream.id}`} className="ui button negative">
                        DELETE
                    </Link>
                </div>
            )
        }
    }

    renderList() {
        return this.props.streams.map(stream => {
            return (
                <div className="ui card" key={stream.id}>
                    <Link to={`/streams/${stream.id}`} className="image" >
                        <img alt="Not Found" src={faker.image.image()}/>
                    </Link> 
                    <div className="content">
                        <Link to={`/streams/${stream.id}`}>
                            {stream.title}
                        </Link>
                        <div className="description">
                            {stream.description}
                        </div>
                        <div className="extra content">
                            {this.renderAdmin(stream)}
                        </div>
                    </div>
                </div>
            )
        })
    }

    render() {
        return (
            <div>
                <div className="ui header">
                    <h2 className="ui left floated header">Streams</h2>
                    <div className="ui right floated header">{this.createButton()}</div>
                </div>
                <div className="ui clearing divider"></div>
                    <div className="ui container" style={{ width: '1210px' }}>
                        <div className="ui cards">{this.renderList()}</div>
                    </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return { 
        streams: Object.values(state.streams), 
        currentUserId: state.auth.userId,
        isSignedIn: state.auth.isSignedIn
    }
}

export default connect(mapStateToProps, { fetchStreams })(StreamList)
